package Loops;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=0;
		
		while(i<5) {
			System.out.println("Hello  "+i);
			i++;
		}
		
		int j=7;
		
		do {
			System.out.println("Do while hello  "+j);
			j++;
		}while(j<5);

	}

}
